session_data.user_stylesheet:
@charset "UTF-8";
/********************/
/* 1. Global styles */
/********************/

a:link {
	color: #ff0000;
}
a:active {
	color: #ff0000;
}
a:visited {
	color: #5f5f5f;
}
body {
	line-height: normal;
	margin: 1.5em;
}
img {
	border:0;
	vertical-align: middle;
}


/*****************************/
/* 2. Article/Chapter Header */
/*****************************/

/* Context information at the top of the page */
/**********************************************/
.ArticleContextInformation, .ChapterContextInformation {
	margin-bottom: 0.83em;
}
.ArticleContextInformation .ContextInformation {
	border-right:  1px solid;
	border-bottom: 1px solid;
	border-left:   1px solid #999999;
	border-top:    1px solid #999999;
	padding: 1px;
	width:auto;
	display:inline-block
}
.ChapterContextInformation .ContextInformation {
	border-right:  1px solid;
	border-bottom: 1px solid;
	border-left:   1px solid #999999;
	border-top:    1px solid #999999;
	padding: 1px;
	width:auto;
	display:inline-block
}
.ArticleContextInformation .ContextInformationJournalTitles, .ChapterContextInformation .ContextInformationSeriesTitles, .ChapterContextInformation .ContextInformationBookTitles {
	margin: 1px;
	padding: 2px;
	border-right:  1px solid #999999;
	border-bottom: 1px solid #999999;
	border-left:   1px solid;
	border-top:    1px solid;
	display: block;
}
.ContextInformationJournalTitles {
	background-color: #d6d6d6;
}
.ArticleContextInformation .JournalTitle, .ArticleContextInformation .JournalSubTitle {
	display:block
}
.ChapterContextInformation .SeriesTitle, .ChapterContextInformation .SeriesSubTitle,
.ChapterContextInformation .xBookTitle, .ChapterContextInformation .BookSubTitle {
	display:block
}
.ChapterContextInformation .BookEdition {
	display:inline
}
.ChapterContextInformation .BookEdition:before {
	content: ', ';
}
.ArticleCopyright, .ChapterCopyright, .ArticleDOI, .ChapterDOI, .ContextInformationAuthorEditorNames {
	margin: 2px 1px 1px 1px;
	padding: 2px;
	border-right:  1px solid #999999;
	border-bottom: 1px solid #999999;
	border-left:   1px solid;
	border-top:    1px solid;
	display:block
}

/* Article/Chapter Categories */
/******************************/
.Categories {
	color: #666666;
	font-family: "Arial", "Helvetica", sans-serif;
	font-weight: bold;
	margin-bottom: 1.0em;
	margin-top: 1.0em;
}
.ArticleCategory, .ChapterCategory {
	font-size: 150%;
}
.ArticleSubCategory, .ChapterSubCategory {
	font-size: 125%;
}

/* Article/Chaper Titles */
/*************************/
.MainTitleSection {
	color: #444444;
	font-family: "Arial", "Helvetica", sans-serif;
	font-weight: bold;
	margin-bottom: 1.2em;
	margin-top: 1.2em;
}
.ArticleSuperTitle {
	font-size: 150%;
}
.ArticleTitle, .ChapterTitle {
	font-size: 175%;
	margin-top: 0;
	margin-bottom: 0;
}
.ArticleSubTitle, .ChapterSubTitle {
	font-size: 150%;
}

/* Author Information */
/**********************/
.AuthorNames {
	font-size: 100%;
	font-weight: bold;
	margin-bottom: 1em;
	margin-top: 1em;
}
.Prefix:after {
	content: '\00a0';
}
.Suffix:before {
	content: '\00a0';
}
.Degrees:before {
	content: ',\00a0';
}
.NativeName:before, .Role:before {
	content: '\00a0(';
}
.NativeName:after, .Role:after {
	content: ')';
}
.Affiliation {
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 80%;
	margin-top:2px;
}
.AffiliationNumber {
	float: left;
	padding-right: 0.5em;
}
.AffiliationText {
	margin-left: 2em;
}
.AffiliationHint {
	font-style: italic;
}
.AuthorNames .DeceasedSymbol:before {
	content: "\2020";
	padding-left: 3px;
}
.AuthorNames .EqualContributionSymbol:before {
	content: "*";
	padding-left: 3px;
}
.AffiliationLegend .DeceasedSymbol:before {
	content: "\2020";
	display: inline-block;
	width: 14px;
}
.AffiliationLegend .EqualContributionSymbol:before {
	content: "*";
	display: inline-block;
	width: 14px;
}
.ClearBoth {
	clear: both;
	font-size: 0pt;
	line-height: 0 !important;
}
.Contact, .AffiliationLegend {
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 80%;
	margin-bottom: 1em;
	margin-top:    1em;
}
.AuthorNames .ContactIcon {
	width:14px;
	height:10px;
	background-image:url('contact.gif');
	background-repeat: no-repeat;
	display: inline-block;
}
.Contact .ContactIcon {
	width:14px;
	height:10px;
	background-image:url('contact.gif');
	background-repeat: no-repeat;
	float:left;
	margin-top:4px;
	margin-right:4px;
}
.ContactAuthorLine {
	padding-left:20px;
	font-weight: bold;
}
.ContactAdditionalLine {
	padding-left:20px;
}
.ContactType {
	font-weight: bold;
}

/* Article/Chapter History */
/***************************/
.ArticleHistory {
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 80%;
	margin-bottom: 1em;
	margin-top: 1em;
}
.HistoryLabel {
	font-weight: bold;
}
.History+.History {
	padding-left: 0.5em;
}

/* Table of Contents */
/*********************/
.ArticleOrChapterToc {
	border-top: 2px groove;
	margin-top: 1em;
	padding-top: 1em;
}
.ArticleOrChapterToc .HeadingNumber {
	font-weight: bold;
	padding-right: 0.3em;
}

/* Abstract */
/************/
.Abstract {
	margin-top: 1em
}
.Abstract>.Heading {
	font-weight: bold;
}
.AbstractSection {
	margin-top: 0.3em
}
.AbstractSection>.Heading {
	font-style: italic;
}

/* Keywords */
/************/
.KeywordGroup {
	margin-top: 1em;
}
.KeywordGroup .Heading {
	font-weight: bold;
	padding-right: 0.5em;
	display: inline;
}
.Keyword+.Keyword:before {
	content: " \2013  ";
}

/* Foreign Language Header */
/***************************/
.ForeignLanguageHeader {
	border-top: 2px groove;
	margin-top: 1em;
	padding-left: 3em;
}

.ForeignLanguageTitleSection {
	color: #444444;
	font-family: "Arial", "Helvetica", sans-serif;
	font-weight: bold;
	margin-bottom: 1.2em;
	margin-top: 1.2em;
}

/* AbbreviationGroup */
/*********************/
.AbbreviationGroup {
	margin-top: 1em;
}
.ForeignLanguageHeader+.AbbreviationGroup {
	border-top: 2px groove;
	margin-top: 1em;
	padding-top: 1em;
}
.AbbreviationGroup>.Heading {
	display: block;
	font-weight: bold;
	margin-bottom: 0em;
}
.AbbreviationGroup .DefinitionList {
	margin-top: 0em;
	margin-bottom: 0em;
	padding-right: 0.1em;
	padding-top: 0.1em;
	padding-bottom: 0.1em;
}
.AbbreviationGroupSection>.Heading {
	display: block;
	font-weight: bold;
	padding-top: 0.2em;
}

/* ArticleNote */
/***************/
.ArticleNote {
	margin-top: 1em;
}
.ArticleNote>.Heading {
	font-style: italic;
}
.ArticleNoteMotto {
	text-align: right;
}

/* Biography */
/*************/
.Biography {
	display: table;
	margin-top: 0.8em;
	padding-bottom: 0;
	width: 100%;
}
.Biography .FormalPara {
	display: table-row-group;
}
.Biography .BiographyFigure {
	display: table-footer-group;
}
.Biography .Figure {
	border-bottom: none;
	margin-top: 0.5em;
	margin-bottom: 0;
}

/*********************/
/* 3. General styles */
/*********************/

/* BlockQuote */
/**************/
.Fulltext>.Para:first-of-type>.BlockQuote:first-of-type {
	margin-top: 0;
}
.BlockQuote>.Heading {
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 100%;
	font-weight: bold;
	margin-top: 1em;
}

/* Caption */
/***********/
.Caption, .TableFooter {
	display: block;
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 80%;
}
.Caption {
	margin-top: 1.0em;
}
.TableFooter {
	margin-top: 1.0em;
}
.Table .Caption {
	margin-top: 0.5em;
}
.Figure .Caption {
	margin-bottom: 1.0em;
}
.Table .Caption {
	margin-bottom: 0.5em;
}
.CaptionNumber {
	font-weight: bold;
	padding-right: 0.6em;
}
.CaptionContent>span.SimplePara {
	display: block;
}
.CaptionContent div:first-of-type {
	display: inline;
	margin-top: 0;
}

/* DefinitionList */
/******************/
.DefinitionList {
	margin-top: 1em;
	margin-bottom: 1em;
	padding: 0.1em;
}
.DefinitionList>.Heading {
	display: block;
	font-style: italic;
	margin-bottom: 0.5em;
}
.DefinitionListEntry {
	padding-left: 1em;
	clear: both;
}
.Term {
	float: left;
	font-style: italic;
	padding-right: 0.5em;
	text-indent: 0;
	vertical-align: top;
}
.Description {
	padding-bottom: 0.3em;
	display:block;
	margin-left: 2em;
}

/* Emphasis */
/************/
.EmphasisTypeBold {
	font-weight: bold;
}
.EmphasisTypeItalic {
	font-style: italic;
}
.EmphasisTypeUnderline {
	text-decoration: underline;
}
.EmphasisTypeDoubleUnderline {
	border-bottom-style:double;
}
.EmphasisTypeSmallCaps {
	font-variant:small-caps;
}
.EmphasisTypeBoldItalic {
	font-weight: bold;
	font-style: italic;
}
.EmphasisTypeBoldUnderline {
	font-weight: bold;
	text-decoration: underline;
}
.EmphasisTypeBoldDoubleUnderline {
	font-weight: bold;
	border-bottom-style:double;
}
.EmphasisTypeBoldSmallCaps {
	font-weight: bold;
	font-variant:small-caps;
}
.EmphasisTypeItalicUnderline {
	font-style: italic;
	text-decoration: underline;
}
.EmphasisTypeItalicDoubleUnderline {
	font-style: italic;
	border-bottom-style: double;
}
.EmphasisTypeItalicSmallCaps {
	font-style: italic;
	font-variant: small-caps;
}
.EmphasisTypeBoldItalicUnderline {
	font-weight: bold;
	font-style: italic;
	text-decoration: underline;
}
.EmphasisTypeBoldItalicDoubleUnderline {
	font-weight: bold;
	font-style: italic;
	border-bottom-style: double;
}
.EmphasisTypeBoldItalicSmallCaps {
	font-weight: bold;
	font-style: italic;
	font-variant: small-caps;
}
.EmphasisTypeUnderlineSmallCaps {
	text-decoration: underline;
	font-variant: small-caps;
}
.EmphasisTypeDoubleUnderlineSmallCaps {
	border-bottom-style: double;
	font-variant: small-caps;
}
.EmphasisFontCategoryNonProportional {
	font-family: 'Courier New', Courier, monospace;
	font-size: 75%;
}
.EmphasisFontCategorySansSerif {
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 75%;
}
.EmphasisTypeStrikethrough {
	text-decoration: line-through;
}

/* Figure */
/**********/
.Figure {
	/*border-bottom: 2px groove;*/
	margin-top: 1em;
	margin-bottom: 1em;
	text-indent: 0;
}
.OpeningFigure .Figure {
	/*padding-bottom: 1em;*/
}
.Figure img {
	height: auto;
	margin: 0;
	max-width: 100%;
}
.MediaObject {
	display: block;
}
.MediaObject+.MediaObject {
	margin-top: 1em;
}

/* Footnotes */
/*************/
.FootnoteSection {
	border-top: 2px groove;
	font-family: "Arial", "Helvetica", sans-serif;
	margin-top: 1em;
}
.FootnoteSection>.Heading {
	display: block;
	font-size: 150%;
	font-weight: bold;
	margin-top: 0.83em;
	margin-bottom: 0.83em;
}
.Footnote {
	clear: both;
	font-size: 90%;
	text-align: left;
}
.FootnoteNumber {
	float:left;
	font-size: 80%;
	padding-right: 0.5em;
	vertical-align: super;
}
.Footnote>.FootnoteNumber {
	font-size: 80%;
	vertical-align: baseline;
}
.FootnoteParas > div {
	margin-left: 1em;
	margin-top: 0.3em;
}

/* Equations */
/*************/
.Equation {
	margin-top: 0.5em;
	margin-bottom: -0.7em !important;
	text-align: justify;
	text-indent: 0;
	vertical-align: middle;
	width: 100%;
}
.Equation:after {
	content: '';
	display: inline-block;
	width: 100%;
}
.EquationContent {
	display: inline-block;
	margin-bottom: 0;
	max-width: 100%;
	text-align: left;
	text-indent: 0;
	vertical-align: middle;
}
.EquationContent img {
	max-width: 100%;
}
.EquationNumber {
	display: inline-block;
	margin-bottom: 0.4em;
	text-align: right;
	vertical-align: middle;
}
.EquationSourceXml {
	white-space: nowrap;
}

/* Headings */
/************/
.HeadingNumber {
	padding-right: 0.3em;
}
.RenderAsSection1>.Heading {
	display: block;
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 150%;
	font-weight: bold;
	margin-top: 0.8em;
	margin-bottom: 0.8em;
}
.RenderAsSection2>.Heading {
	display: block;
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 125%;
	font-weight: bold;
	margin-top: 0.8em;
	margin-bottom: 0.8em
}
.RenderAsSection3>.Heading {
	display: block;
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 115%;
	font-weight: bold;
	margin-top: 0.8em;
	margin-bottom: 0.8em;
}
.RenderAsSection4>.Heading {
	display: block;
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 105%;
	font-style: italic;
	font-weight: bold;
	margin-top: 0.8em;
	margin-bottom: 0.8em
}
.RenderAsSection5>.Heading {
	display: block;
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 100%;
	font-weight: bold;
	margin-top: 0.8em;
	margin-bottom: 0.8em;
}
.RenderAsSection6>.Heading {
	display: block;
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 100%;
	font-weight: bold;
	margin-top: 0.8em;
	margin-bottom: 0.8em;
}
.RenderAsSection7>.Heading {
	display: block;
	font-family: "Arial", "Helvetica", sans-serif;
	font-size: 100%;
	font-weight: bold;
	margin-top: 0.8em;
	margin-bottom: 0.8em;
}
.SectionTypeBox>.Heading, .SectionTypeOverview>.Heading, .SectionTypeWarning>.Heading {
	margin-top: 8px;
}

/* FormalPara */
/**************/
.FormalPara {
	margin-top: 1em;
	margin-bottom: 1em;
}
.FormalParaRenderingStyle1>.Heading{
	font-weight: bold;
	padding-right: 1em;
}
.FormalParaRenderingStyle2>.Heading {
	font-style: italic;
	padding-right: 1em;
}
.FormalParaRenderingStyle3>.Heading {
	font-style: italic;
	padding-right: 1em;
}

/* Literal */
/***********/
.Literal {
	font-family: 'Courier New', Courier, monospace
}

/* Ordered List */
/****************/
.OrderedList {
	margin-top: 0.5em;
	margin-bottom: 0.5em;
}
.OrderedList .Heading {
	font-style: italic;
	margin-top: 1em;
}
.OrderedList .Heading+.ListItem {
	margin-top: 0;
}
.ListItem {
	margin-left: 0em;
	padding-top: 0.5em;
}
.ItemNumber {
	float: left;
	padding-right: 0.5em;
}
.ItemContent {
	overflow: hidden;
}

/* Para */
/********/
.Fulltext>.Para:first-of-type {
	border-top: 2px groove;
	margin-top: 1em;
	padding-top: 1em;
}
.Para+.Para {
	margin-top: 0.5em;
}
.ParaTextBreak {
	margin-top: 1em !important;
	text-indent: 0 !important;
}
.ParaTypeExample, .ParaTypeTip {
	border-left: 4px solid;
	padding-left: 0.5em;
	margin-top: 1em !important;
	margin-bottom: 1em;
}
.ParaTypeEyeCatcher, .ParaTypeImportant, .ParaTypeWarning {
	background: #bbbbbb;
	border: 2px solid;
	padding: 0.5em;
	margin-top: 0.8em !important;
	margin-bottom: 0.8em;
}
.ParaTypeGeneticSequence, .ParaTypeLiteral, .ParaTypeProgramcode {
	font-family: 'Courier New', Courier, monospace;
}
.ParaTypeProgramcode {
	text-indent: 0 !important;
	white-space: pre-wrap;
}
.ParaTypeLegalText {
	font-weight: bold;
	text-align: left;
}
.ParaTypeOverview {
	border: 2px solid;
	margin-top: 0.8em !important;
	margin-bottom: 0.8em;
	padding: 0.5em
}
.ParaTypeSource {
	font-style: italic;
}
.ParaTypeTrailer {
	font-weight: bold;
	padding-left: 0.5em;
	padding-right: 0.5em;
}


/* ProgramCode */
/***************/
.ProgramCode {
	margin-top: 1em;
	margin-bottom: 1em;
}
.ProgramCode .FixedLine {
	font-family: 'Courier New', Courier, monospace;
	font-size: 95%;
}
.LineGroup+.LineGroup {
	margin-top: 1em;
}

/* Quiz */
/********/
.Heading+.Para>.Quiz>.Heading {
	margin-top: 0; /* omit space above quiz heading if it follows another heading */
}
.Quiz .Heading {
	font-weight: bold;
	margin-top: 1em;
	text-indent: 0;
}
.QuizTask {
	display: table;
	margin-top: 0.5em;
	margin-bottom: 0.5em;
	width: 100%;
	text-indent: 0;
}
.QuizTask + .QuizTask {
	margin-top: 1.5em;
}
.QuizItemTypeSolution {
	background-color: #eeeeee;
	border: 2px solid #aaaaaa;
	margin-top: 0.5em;
	padding: 10px;
}

a.QuizHide:link, a.QuizHide:active, a.QuizHide:visited, a.QuizShow:link, a.QuizShow:active, a.QuizShow:visited {
	color: #000000;
}
.QuizHide, .QuizShow {
	font-weight: bold;
}
.QuizHide {                   /* hide the "hide button" */
	display: none;
}
.QuizShow:focus + .QuizHide { /* if the focus is on the "show" button, show the "hide" button */
	display: inline;
}
.QuizShow:focus {             /* if the focus is on the "show" button, hide the "show" button */
	display: none;
}
.QuizItemTypeSolution .ItemContent { /* hide the solution */
	display: none;
}
.QuizShow:focus ~ .ItemContent {     /* if the focus is on the "show" button, show the solution */
	display: inline;
}

/* Section Authors */
/*******************/
.Section1 .AuthorNames, .Section2 .AuthorNames, .Section3 .AuthorNames {
	margin-top: 0.3em;
	margin-bottom: 0.5em;
}

/* Section Types */
/*****************/
.SectionTypeBox, .SectionTypeOverview {
	border: 2px solid !important;
	margin-top: 1em !important;
	padding-left: 0.5em;
	padding-right: 0.5em;
	padding-bottom: 0.5em;
}
.SectionTypeExample, .SectionTypeTip {
	border-left: 4px solid;
	margin-top: 1em !important;
	padding-top: 0em;
	padding-left: 0.5em;
	padding-right: 0.5em;
	padding-bottom: 0.5em;
}
.SectionTypeExample>.Heading {
}
.SectionTypeLegalText, .SectionTypeTrailer {
	font-weight: bold;
	text-align: left;
}
.SectionTypeWarning {
	background: #bbbbbb;
	border: 2px solid;
	margin-top: 1em !important;
	padding-left: 0.5em;
	padding-right: 0.5em;
	padding-bottom: 0.5em;
}
.RenderAsSection1 {
	border-top: 2px groove;
	margin-top: 1em;
}
.SectionTypeBox+.RenderAsSection1, .SectionTypeOverview+.RenderAsSection1, .SectionTypeWarning+.RenderAsSection1 {
	border-top: 0; /* no bar above the section if the previous section is in a box */
}

/* Sidebars */
/************/
.InternalRefSidebarLexicon {
	background-image:url('sidebar.gif');
	background-repeat: no-repeat;
	font-weight: bold;
	padding-left:20px;
}
.SidebarDialog {
	border: 1px solid;
	margin-left: 2em;
	margin-top: 0.2em;
	padding: 0.2em;
	text-indent: 0;
}
.SidebarNumber {
	font-weight: bold;
	padding-right: 0.5em;
}

/* SimplePara */
/**************/
.SimplePara+.SimplePara {
	margin-top: 0.5em;
}

/* Stack */
/*********/
.Stack {
	display: inline-table;
	vertical-align: top;
}
.Stack sub {
	display: table-row;
}
.Stack sup {
	display: table-row;
}

/* Tables */
/**********/
thead {
	background: #bbbbbb;
	color: #000000 !important;
}
.Table {
	margin-bottom: 1em;
	text-indent: 0;
}
.Table img {
	height: auto;
	margin: 0;
	max-width: 100%;
}
.Para+.Table, .Para>.Table {
	margin-top: 1em;
}
.SimplePara>.Table {
	margin-top: .5em;
}
table {
	text-indent: 0;
}
thead, tbody {
	vertical-align: top;
}

/* UnorderedList */
/*****************/
.UnorderedList>.Heading {
	display: block;
	font-style: italic;
	margin-top: 1em;
}
.Heading+ul {
	margin-top: 0;
}
ul {
	margin-top: 0.5em;
	margin-bottom: 0.5em;
}
ul.UnorderedListMarkBullet {
	list-style-type: disc
}

ul.UnorderedListMarkBullet div {
	margin-bottom: 0.3em;
}
ul.UnorderedListMarkBullet .Equation div {
	margin-bottom: 0;
}

ul.UnorderedListMarkNone {
	list-style-type: none
}

ul.UnorderedListMarkNone div {
	margin-bottom: 0.3em;
}
ul.UnorderedListMarkNone .Equation div {
	margin-bottom: 0;
}

ul.UnorderedListMarkDash {
	list-style: none;
	margin-left: 1.9em;
	padding-left: 1em;
	text-indent: -1em;
}
ul.UnorderedListMarkDash > li > div:first-of-type {
	display:inline;
}

ul.UnorderedListMarkDash div {
	text-indent: 0;
	margin-top: 0.3em;
	margin-bottom: 0.3em;
}

ul.UnorderedListMarkDash>li:before {
	content: "\2013 \0020";
}

/* Verse */
/*********/
.Verse {
	margin-top: 1em;
	margin-bottom: 1em;
}
