/**********************/
/* 5. BookFrontmatter */
/**********************/

/* Cover */
/*********/
.CoverFigure img {
	/* autoscale cover figure, used by the EPUB converter */
	margin: 0 auto 6px;
	width: 100%;
	max-width: 480px;
	height: auto;
}

/* Series Title Page */
/*********************/
.SeriesTitlePage {
	border-bottom: 2px groove;
	padding-bottom: 1.5em;
	padding-top: 1em;
}
.SeriesTitlePage .VolumeNumber, .SeriesTitlePage .SeriesTitle {
	display: block;
	font-size: 175%;
	font-weight: bold;
	margin-top: 5px;
	text-align: left !important;
}
.SeriesTitlePage .SeriesSubTitle {
	display: block;
	font-size: 150%;
	font-weight: bold;
	margin-top: 5px;
	text-align: left !important;
}
.SeriesTitlePage .SubSeriesTitle {
	display: block;
	font-size: 150%;
	font-weight: bold;
	margin-top: 15px;
	text-align: left !important;
}
.SeriesTitlePage .SubSeriesSubTitle {
	display: block;
	font-size: 125%;
	font-weight: bold;
	margin-top: 5px;
	text-align: left !important;
}
.SeriesTitlePage .CollaboratorDesignation {
	display: block;
	font-weight: bold;
	padding-top: 3em;
}
.SeriesTitlePage .Collaborators {
	display: block;
}
.SeriesTitlePage .AffiliationText {
	margin-left: 0
}
.SeriesTitlePage .Affiliation+.CollaboratorName {
	display: inline-block;
	margin-top: 1em;
}
.SeriesInformationText {
	padding-top: 3em;
}

/* BookTitlePage */
/*****************/
.BookTitlePage {
	border-bottom: 2px groove;
	padding-top: 1em;
}
.BookTitlePageAfterSeriesTitlePage {

	break-before: always; -webkit-column-break-before: always;
}
.BookContextInformation + .BookTitlePage {
	border-top: none;
	padding-top: 0;
}
.BookTitlePage .BookEditors, .BookTitlePage .BookAuthors {
	margin-top: 5px;
	text-align: left !important;
}
.BookTitlePage .BookEditorGroup .CollaboratorDesignation, .BookTitlePage .Editor,
.BookTitlePage .InstitutionalEditor, .BookTitlePage .Author, .BookTitlePage .InstitutionalAuthor {
	font-size: 105%;
	font-style: italic;
	margin-bottom: 5px;
}
.BookTitlePage .BookTitle {
	display: block;
	font-size: 175%;
	font-weight: bold;
	margin-top: 1em;
	text-align: left !important;
}
.BookTitlePage .BookSubTitle {
	display: block;
	font-size: 150%;
	font-weight: bold;
	margin-top: 0.5em;
	margin-bottom: 0.5em;
	text-align: left !important;
}
.BookTitlePage .BookEdition {
	display: block;
	font-size: 125%;
	font-weight: bold;
}
.BookTitlePage .CollaboratorLine {
	padding-top: 0.5em;
}
.BookFeatureText {
	margin-top: 0.5em;
}
/*.BookFeatureText+.BookFeatureText {
	margin-top: 0;
}*/
.BookTitlePage .PublisherLogo .Figure, .BookTitlePage .PublisherImprintName {
	font-size: 125%;
	margin-top: 4em;
	margin-bottom: 1.5em;
	text-align: center;
}

/* Copyright Page */
/******************/
.CopyrightPage {
	border-bottom: 2px groove;
	padding-top: 1em;
	padding-bottom: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.CopyrightPage .CollaboratorDesignation {
	display: block;
	font-style: italic;
	padding-bottom: 0.5em;
}
.CopyrightPage .AuthorGroup, .CopyrightPage .EditorGroup {
	padding-bottom: 4em;
}
.CopyrightPage .Author + .Author, .CopyrightPage .Author + .InstitutionalAuthor,
.CopyrightPage .InstitutionalAuthor + .Author, .CopyrightPage .InstitutionalAuthor + .InstitutionalAuthor,
.CopyrightPage .Editor + .Editor, .CopyrightPage .Editor + .InstitutionalEditor,
.CopyrightPage .InstitutionalEditor + .Editor, .CopyrightPage .InstitutionalEditor + .InstitutionalEditor {
	padding-top: 1em;
}
.CopyrightPage .Affiliation {
	font-family: "Times New Roman", "Times", serif;
	margin-top: 0px;
}
.CopyrightPage .AffiliationText {
	margin-left: 0
}
.CopyrightPage .CollaboratorLine {
	padding-top: 1em;
}
.CopyrightPage .CollaboratorLine .CollaboratorDesignation {
	display: inline;
	font-style: normal;
}
.BookFrontmatter .CopyrightPage .ArticleNoteESMHint {
	border: none;
	padding-bottom: 0;
	break-before: auto; -webkit-column-break-before: auto;
}
.CopyrightPageISBNs, .CopyrightPageISSNs, .SpringerLocationsLine, .CatalogingInformation, .MathematicsSubjectClassification,
.CopyrightLine, .CopyrightPage .CopyrightComment, .CopyrightStandardText, .TrademarkQualifierText,
.ProductLiability, .LegalNotice, .CoverDesigner, .BookCoverFigureText, .PaperInfo, .SpringerReferenceLine {
	margin-top: 1em;
}
.CopyrightPagePrintISSN+.CopyrightPageElectronicISSN {
	padding-left: 1.5em;
}
.CopyrightPagePrintISBN+.CopyrightPageElectronicISBN {
	padding-left: 1.5em;
}
.MathematicsSubjectClassificationCode + .MathematicsSubjectClassificationCode:before {
	content: ", ";
}

/* Frontispiece */
/****************/
.Frontispiece {
	border-bottom: 2px groove;
	padding-top: 1em;
	padding-bottom: 1em;
	break-before: always; -webkit-column-break-before: always;
}

/* Dedication */
/**************/
.Dedication {
	border-bottom: 2px groove;
	padding-bottom: 1em;
	padding-top: 1em;
	break-before: always; -webkit-column-break-before: always;
}

/* Foreword */
/************/
.Foreword {
	border-bottom: 2px groove;
	padding-top: 1em;
	padding-bottom: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.ForewordTitle {
	font-size: 150%;
	font-weight: bold;
	padding-bottom: 0.5em;
	break-after: avoid; -webkit-column-break-after: avoid;
	text-align: left !important;
}
.Foreword .AuthorName, .ForewordLocations, .ForewordDate {
	font-weight: bold;
	margin-top: 0.2em;
	text-align: right;
}
.ForewordLocation+.ForewordLocation:before {
	content: ", ";
}
.Foreword .RenderAsSection1>.Heading {
	font-size: 125%;
}
.Foreword .RenderAsSection2>.Heading, .Foreword .RenderAsSection3>.Heading {
	font-size: 105%;
}

/* Preface */
/***********/
.Preface {
	border-bottom: 2px groove;
	padding-top: 1em;
	padding-bottom: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.PrefaceTitle {
	font-size: 150%;
	font-weight: bold;
	padding-bottom: 0.5em;
	break-after: avoid; -webkit-column-break-after: avoid;
	text-align: left !important;
}
.Preface .AuthorName, .PrefaceLocations, .PrefaceDate {
	font-weight: bold;
	margin-top: 0.2em;
	text-align: right;
}
.PrefaceLocation+.PrefaceLocation:before {
	content: ", ";
}
.Preface .RenderAsSection1>.Heading {
	font-size: 125%;
}
.Preface .RenderAsSection2>.Heading, .Preface .RenderAsSection3>.Heading {
	font-size: 105%;
}

/* ArticleNote */
/***************/
.BookFrontmatterArticleNote {
	/* .BookFrontmatter .ArticleNote doesn't work on Kindle */
	border-bottom: 2px groove;
	padding-bottom: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.PartFrontmatter .ArticleNote {
	padding-bottom: 1em;
}
.BookFrontmatter .ArticleNote .Heading, .PartFrontmatter .ArticleNote .Heading {
	font-size: 150%;
	font-style: normal;
	font-weight: bold;
	padding-bottom: 0.5em;
	break-after: avoid; -webkit-column-break-after: avoid;
	text-align: left !important;
}

/* BookAcknlowledgments */
/************************/
.BookAcknowledgments {
	border-bottom: 2px groove;
	padding-top: 1em;
	padding-bottom: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.BookAcknowledgmentsTitle {
	font-size: 150%;
	font-weight: bold;
	padding-bottom: 0.5em;
	break-after: avoid; -webkit-column-break-after: avoid;
	text-align: left !important;
}
.BookAcknowledgments .AuthorName {
	font-weight: bold;
	margin-top: 1em;
}
.BookAcknowledgments .RenderAsSection1>.Heading {
	font-size: 125%;
}
.BookAcknowledgments .RenderAsSection2>.Heading, .BookAcknowledgments .RenderAsSection3>.Heading {
	font-size: 105%;
}

/* BookNotes */
/*************/
.BookNotes {
	border-bottom: 2px groove;
	padding-top: 1em;
	padding-bottom: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.BookNotesTitle {
	font-size: 150%;
	font-weight: bold;
	margin-bottom: 0.5em;
	break-after: avoid; -webkit-column-break-after: avoid;
	text-align: left !important;
}
.BookNotes .AuthorName {
	font-weight: bold;
	margin-bottom: 0.5em;
}
.BookNotesBody {
	margin-top: 1em;
}

/* Abbreviation Group */
/**********************/
.BookFrontmatterAbbreviationGroup {
	/* .BookFrontmatter .AbbreviationGroup doesn't work on Kindle */
	border-bottom: 2px groove;
	margin-top: 1em;
	padding-bottom: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.PartFrontmatter .AbbreviationGroup {
	padding-bottom: 1em;
	margin-top: 1em;
}
.BookFrontmatter .AbbreviationGroup .Heading, .PartFrontmatter .AbbreviationGroup .Heading {
	font-size: 150%;
	font-weight: bold;
	padding-bottom: 0.5em;
	break-after: avoid; -webkit-column-break-after: avoid;
	text-align: left !important;
}
.BookFrontmatter .AbbreviationGroupSection>.Heading, .PartFrontmatter .AbbreviationGroupSection>.Heading {
	font-size: 125%;
	margin-top: 0.5em;
	padding-bottom: 0em;
}

/* Table of Contents */
/*********************/
.Toc {
	border-bottom: 2px groove;
	border-top: none;
	margin-top: 0em;
	padding-bottom: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.BookContextInformation+.Appendix>.TocAppendix:first-child {
	/* omit the top border of the first section1 of the first
	appendix if it immediately follows the book context information
	(otherwise there would be two lines) */
	border-top: none;
	padding-top: 0;
}
.Toc .Headings {
	font-family: "Times New Roman", "Times", serif;
	font-weight: bold;
	text-align: left !important;
}
.TocAppendix .Headings {
	font-family: "Times New Roman", "Times", serif;
	font-weight: normal;
	text-align: left !important;
}
.Toc>.Headings>.Heading, .TocAppendix>.Headings>.Heading {
	font-size: 150%;
}
.Toc>.Headings>.SubHeading, .TocAppendix>.Headings>.SubHeading {
	font-size: 125%;
	text-align: left !important;
}
.TocEntry, .TocPart, .TocBack {
	text-align: left !important;
}
.TocSection1, .TocSection2, .TocSection3, .TocSection4, .TocSection5, .TocSection6, .TocSection7 {
	padding-left: 1.5em;
}

/* List of Headings */
/********************/
.Loh {
	border-bottom: 2px groove;
	padding-bottom: 1em;
	padding-top: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.Loh .Headings {
	font-weight: bold;
	margin-bottom: 1em;
	text-align: left !important;
}
.Loh>.Headings>.Heading {
	display: block;
	font-size: 150%;
}
.Loh>.Headings>.SubHeading {
	display: block;
	font-size: 125%;
}
.LohPageNumber {
	/*float: right;*/
	display: none;
}

/* List of Contributors */
/************************/
.Loc {
	border-bottom: 2px groove;
	padding-bottom: 1em;
	padding-top: 1em;
	break-before: always; -webkit-column-break-before: always;
}
.Loc .Headings {
	font-weight: bold;
	margin-bottom: 1em;
	text-align: left !important;
}
.Loc>.Headings>.Heading {
	display: block;
	font-size: 150%;
}
.Loc>.Headings>.SubHeading {
	display: block;
	font-size: 125%;
}
.LocDiv {
	margin-top: 1em;
}
.LocDiv .Heading {
	font-size: 130%;
	text-align: left !important;
}
.LocDiv .SubHeading {
	font-size: 105%;
	font-style: italic;
	font-weight: normal;
	text-align: left !important;
}
.LocDiv .FormalPara .Heading {
	font-size: 100%;
}
.Loc .AuthorGroup+.AuthorGroup, .Loc .AuthorGroup+.EditorGroup, .Loc .EditorGroup+.AuthorGroup, .Loc .EditorGroup+.EditorGroup {
	margin-top: 1em;
}
.Loc .Author, .Loc .Editor, .Loc .InstitutionalAuthor, .Loc .InstitutionalEditor {
	font-weight: bold;
}
.Loc .AffiliationText {
	font-family: "Times New Roman", "Times", serif;
	font-size: 100%;
	margin-left: 0;
}
.Loc .Email + .Email:before {
	content: ", ";
}
.Loc .Emails, .Loc .URLs {
	display: inline;
}
.Loc .Emails + .URLs:before {
	content: ", ";
}
.Loc .URL + .URL:before {
	content: ", ";
}

/* BookFrontmatter FootnoteSection */
/***********************************/
.BookFrontmatterFootnoteSection {
	/* .BookFrontmatter .FootnoteSection doesn't work on Kindle */
	border-top: none;
	margin-top: 0em;
	break-before: always; -webkit-column-break-before: always;
}
.PartFrontmatter .FootnoteSection {
	border-top: none;
	margin-top: 0em;
}
.BookFrontmatter .FootnoteSection>.Heading, .PartFrontmatter .FootnoteSection>.Heading {
	font-weight: bold;
}

/* Colophon */
/**************/
.Colophon {
	border-top: 2px groove;
	margin-top: 1em;
	padding-top: 0.8em;
	font-style: italic;
}

/* ConferenceInfo */
/******************/
.ConferenceInfo {
	border-top: 2px groove;
	margin-top: 1em;
	padding-top: 0.8em;
}
.ConfEventAbbreviation, .ConfNumber {
	display: inline;
}
.ConfNumber:before {
	content: ' ';
}
.ConfEventLocation .City:after {
	content: ', ';
}
.ConfEventDateEnd:before {
	content: '\2013';
}
.ConfEventDateStart, .ConfEventDateEnd {
	display: inline;
}
.ConfEventDateStart .Year:after, .ConfEventDateStart .Month:after, .ConfEventDateEnd .Year:after, .ConfEventDateEnd .Month:after {
	content: '/';
}
