# Options to customize the e-book viewer

### Begin group: DEFAULT
 
# remember window size
# Remember last used window size
remember_window_size = False
 
# user css
# Set the user CSS stylesheet. This can be used to customize the look of all books.
user_css = u'@import url(http://fonts.googleapis.com/css?family=Inconsolata);\n@import url(http://fonts.googleapis.com/css?family=PT+Sans);\n@import url(http://fonts.googleapis.com/css?family=PT+Sans+Narrow:400,700);\narticle,\naside,\ndetails,\nfigcaption,\nfigure,\nfooter,\nheader,\nhgroup,\nnav,\nsection,\nsummary {\n  display: block;\n}\naudio,\ncanvas,\nvideo {\n  display: inline-block;\n}\naudio:not([controls]) {\n  display: none;\n  height: 0;\n}\n[hidden] {\n  display: none;\n}\nhtml {\n  font-family: sans-serif;\n  -webkit-text-size-adjust: 100%;\n  -ms-text-size-adjust: 100%;\n}\nbody {\n  margin: 0;\n}\na:focus {\n  outline: thin dotted;\n}\na:active,\na:hover {\n  outline: 0;\n}\nh1 {\n  font-size: 2em;\n}\nabbr[title] {\n  border-bottom: 1px dotted;\n}\nb,\nstrong {\n  font-weight: bold;\n}\ndfn {\n  font-style: italic;\n}\nmark {\n  background: #ff0;\n  color: #000;\n}\ncode,\nkbd,\npre,\nsamp {\n  font-family: monospace, serif;\n  font-size: 1em;\n}\npre {\n  white-space: pre-wrap;\n  word-wrap: break-word;\n}\nq {\n  quotes: "\\201C" "\\201D" "\\2018" "\\2019";\n}\nsmall {\n  font-size: 80%;\n}\nsub,\nsup {\n  font-size: 75%;\n  line-height: 0;\n  position: relative;\n  vertical-align: baseline;\n}\nsup {\n  top: -0.5em;\n}\nsub {\n  bottom: -0.25em;\n}\nimg {\n  border: 0;\n}\nsvg:not(:root) {\n  overflow: hidden;\n}\nfigure {\n  margin: 0;\n}\nfieldset {\n  border: 1px solid #c0c0c0;\n  margin: 0 2px;\n  padding: 0.35em 0.625em 0.75em;\n}\nlegend {\n  border: 0;\n  padding: 0;\n}\nbutton,\ninput,\nselect,\ntextarea {\n  font-family: inherit;\n  font-size: 100%;\n  margin: 0;\n}\nbutton,\ninput {\n  line-height: normal;\n}\nbutton,\nhtml input[type="button"],\ninput[type="reset"],\ninput[type="submit"] {\n  -webkit-appearance: button;\n  cursor: pointer;\n}\nbutton[disabled],\ninput[disabled] {\n  cursor: default;\n}\ninput[type="checkbox"],\ninput[type="radio"] {\n  box-sizing: border-box;\n  padding: 0;\n}\ninput[type="search"] {\n  -webkit-appearance: textfield;\n  -moz-box-sizing: content-box;\n  -webkit-box-sizing: content-box;\n  box-sizing: content-box;\n}\ninput[type="search"]::-webkit-search-cancel-button,\ninput[type="search"]::-webkit-search-decoration {\n  -webkit-appearance: none;\n}\nbutton::-moz-focus-inner,\ninput::-moz-focus-inner {\n  border: 0;\n  padding: 0;\n}\ntextarea {\n  overflow: auto;\n  vertical-align: top;\n}\ntable {\n  border-collapse: collapse;\n  border-spacing: 0;\n}\nhtml {\n  font-family: \'PT Sans\', sans-serif;\n}\npre,\ncode {\n  font-family: \'Inconsolata\', sans-serif;\n}\nh1,\nh2,\nh3,\nh4,\nh5,\nh6 {\n  font-family: \'PT Sans Narrow\', sans-serif;\n  font-weight: 700;\n}\nhtml {\n  background-color: #eee8d5;\n  color: #657b83;\n  margin: 1em;\n}\nbody {\n  background-color: #fdf6e3;\n  margin: 0 auto;\n  max-width: 23cm;\n  border: 1pt solid #93a1a1;\n  padding: 1em;\n}\ncode {\n  background-color: #eee8d5;\n  padding: 2px;\n}\na {\n  color: #b58900;\n}\na:visited {\n  color: #cb4b16;\n}\na:hover {\n  color: #cb4b16;\n}\nh1 {\n  color: #d33682;\n}\nh2,\nh3,\nh4,\nh5,\nh6 {\n  color: #859900;\n}\npre {\n  background-color: #fdf6e3;\n  color: #657b83;\n  border: 1pt solid #93a1a1;\n  padding: 1em;\n  box-shadow: 5pt 5pt 8pt #eee8d5;\n}\npre code {\n  background-color: #fdf6e3;\n}\nh1 {\n  font-size: 2.8em;\n}\nh2 {\n  font-size: 2.4em;\n}\nh3 {\n  font-size: 1.8em;\n}\nh4 {\n  font-size: 1.4em;\n}\nh5 {\n  font-size: 1.3em;\n}\nh6 {\n  font-size: 1.15em;\n}\n.tag {\n  background-color: #eee8d5;\n  color: #d33682;\n  padding: 0 0.2em;\n}\n.todo,\n.next,\n.done {\n  color: #fdf6e3;\n  background-color: #dc322f;\n  padding: 0 0.2em;\n}\n.tag {\n  -webkit-border-radius: 0.35em;\n  -moz-border-radius: 0.35em;\n  border-radius: 0.35em;\n}\n.TODO {\n  -webkit-border-radius: 0.2em;\n  -moz-border-radius: 0.2em;\n  border-radius: 0.2em;\n  background-color: #2aa198;\n}\n.NEXT {\n  -webkit-border-radius: 0.2em;\n  -moz-border-radius: 0.2em;\n  border-radius: 0.2em;\n  background-color: #268bd2;\n}\n.ACTIVE {\n  -webkit-border-radius: 0.2em;\n  -moz-border-radius: 0.2em;\n  border-radius: 0.2em;\n  background-color: #268bd2;\n}\n.DONE {\n  -webkit-border-radius: 0.2em;\n  -moz-border-radius: 0.2em;\n  border-radius: 0.2em;\n  background-color: #859900;\n}\n.WAITING {\n  -webkit-border-radius: 0.2em;\n  -moz-border-radius: 0.2em;\n  border-radius: 0.2em;\n  background-color: #cb4b16;\n}\n.HOLD {\n  -webkit-border-radius: 0.2em;\n  -moz-border-radius: 0.2em;\n  border-radius: 0.2em;\n  background-color: #d33682;\n}\n.NOTE {\n  -webkit-border-radius: 0.2em;\n  -moz-border-radius: 0.2em;\n  border-radius: 0.2em;\n  background-color: #d33682;\n}\n.CANCELLED {\n  -webkit-border-radius: 0.2em;\n  -moz-border-radius: 0.2em;\n  border-radius: 0.2em;\n  background-color: #859900;\n}\n\n'
 
# max fs width
# Set the maximum width that the book's text and pictures will take when in fullscreen mode. This allows you to read the book text without it becoming too wide.
max_fs_width = 800
 
# max fs height
# Set the maximum height that the book's text and pictures will take when in fullscreen mode. This allows you to read the book text without it becoming too tall. Note that this setting only takes effect in paged mode (which is the default mode).
max_fs_height = -1
 
# fit images
# Resize images larger than the viewer window to fit inside it
fit_images = True
 
# hyphenate
# Hyphenate text
hyphenate = False
 
# hyphenate default lang
# Default language for hyphenation rules
hyphenate_default_lang = u'en_us'
 
# search online url
# The URL to use when searching for selected text online
search_online_url = u'https://www.google.com/search?q={text}'
 
# remember current page
# Save the current position in the document, when quitting
remember_current_page = True
 
# copy bookmarks to file
# Copy bookmarks to the e-book file for easy sharing, if possible
copy_bookmarks_to_file = True
 
# wheel flips pages
# Have the mouse wheel turn pages
wheel_flips_pages = False
 
# wheel scroll fraction
# Control how much the mouse wheel scrolls by in flow mode
wheel_scroll_fraction = 100
 
# line scroll fraction
# Control how much the arrow keys scroll by in flow mode
line_scroll_fraction = 100
 
# tap flips pages
# Tapping on the screen turns pages
tap_flips_pages = True
 
# line scrolling stops on pagebreaks
# Prevent the up and down arrow keys from scrolling past page breaks
line_scrolling_stops_on_pagebreaks = False
 
# page flip duration
# The time, in seconds, for the page flip animation. Default is half a second.
page_flip_duration = 0.5
 
# font magnification step
# The amount by which to change the font size when clicking the font larger/smaller buttons. Should be a number between 0 and 1.
font_magnification_step = 0.2
 
# fullscreen clock
# Show a clock in fullscreen mode.
fullscreen_clock = False
 
# fullscreen pos
# Show reading position in fullscreen mode.
fullscreen_pos = False
 
# fullscreen scrollbar
# Show the scrollbar in fullscreen mode.
fullscreen_scrollbar = True
 
# start in fullscreen
# Start viewer in full screen mode
start_in_fullscreen = False
 
# show fullscreen help
# Show full screen usage help
show_fullscreen_help = True
 
# cols per screen
cols_per_screen = 1
 
# cols per screen portrait
cols_per_screen_portrait = 1
 
# cols per screen landscape
cols_per_screen_landscape = 1
 
# cols per screen migrated
cols_per_screen_migrated = True
 
# use book margins
use_book_margins = False
 
# top margin
top_margin = 20
 
# side margin
side_margin = 40
 
# bottom margin
bottom_margin = 20
 
# text color
text_color = None
 
# background color
background_color = None
 
# show controls
show_controls = True
 

### Begin group: FONTS
# Font options
 
# serif family
# The serif font family
serif_family = u'Liberation Serif'
 
# sans family
# The sans-serif font family
sans_family = u'Liberation Sans'
 
# mono family
# The monospace font family
mono_family = u'Liberation Mono'
 
# default font size
# The standard font size in px
default_font_size = 20
 
# mono font size
# The monospace font size in px
mono_font_size = 16
 
# standard font
# The standard font type
standard_font = u'serif'
 
# minimum font size
# The minimum font size in px
minimum_font_size = 8
 


